// MapComponent.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import markerIcon from './images/pinpoint.png'; // Update this path to the location where you saved 'marker.png'

const customMarkerIcon = new L.Icon({
  iconUrl: markerIcon,
  iconRetinaUrl: markerIcon,
  iconSize: new L.Point(30, 50), // Size of the icon, adjust as needed
  className: 'leaflet-custom-marker'
});

const MapComponent = ({ coffeeShops }) => {
  const defaultPosition = [37.7749, -122.4194]; // Coordinates for San Francisco

  return (
    <MapContainer center={defaultPosition} zoom={13} style={{ height: '100vh', width: '50%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {coffeeShops.map((shop, idx) => (
        <Marker key={idx} position={[shop.latitude, shop.longitude]}>
          <Popup>{shop.name}</Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default MapComponent;
